          //if(dType == ''DOUBLE'' || dType == ''INTEGER'' || dType == ''CURRENCY'' || dType == ''DATE'' || dType == ''DATETIME'' || dType == ''STRING''){

          String fieldName = fr.getName();
          parentObjFields.add(
            new SelectOption(
              fieldName,
              String.valueOf(fr.getLabel()) +
              '' ('' +
              fieldName +
              '')''
            )
          );
          //}
        }
