      new List<Account>{ acc },
      Contact.AccountId,
      new List<List<Contact>>{ new List<Contact>{ contact1, contact2 } }
    ))[0];

    //Then
    System.assertEquals(acc.Id, accWithRelationships.Id);
    System.assertEquals(acc.Name, accWithRelationships.Name);
