    firstMock.add(''1-1'');
    firstMock.add(''1-2'');
    firstMock.add(''1-3'');
    firstMock.add(''1-4'');

    // Then
    ((fflib_MyList.IList) inOrder1.verify(firstMock, MY_MOCKS.calls(1)))
      .add(''1-1'');
    ((fflib_MyList.IList) inOrder1.verify(firstMock, MY_MOCKS.calls(1)))
      .add(''1-2'');
    ((fflib_MyList.IList) inOrder1.verify(firstMock, MY_MOCKS.calls(1)))
      .add(''1-3'');
    ((fflib_MyList.IList) inOrder1.verify(firstMock, MY_MOCKS.calls(1)))
      .add(''1-4'');

    assertVerifyInOrderExceptionForAddMethod(
      ''1-2'',
      inOrder1,
      firstMock,
      ''It should fail because 1-2 is in the wrong order'',
      1,
      0
    );
  }

  @isTest
